let checkbox=document.getElementById("toggle"),logo=document.getElementsByClassName("header__logo")[0],lang=document.getElementsByClassName("header__lang")[0];checkbox.onchange=e=>{checkbox.checked?window.matchMedia("(max-width: 425px)").matches?(lang.style.display="flex",logo.style.display="none"):window.matchMedia("(max-width: 700px)").matches?logo.style.display="none":window.matchMedia("(max-width: 768px)").matches?lang.style.display="flex":window.matchMedia("(max-width: 1600px)").matches&&(lang.style.display="none"):(logo.style.display="",lang.style.display="")},$(document).ready((function(){$(".design__box").slick({slidesToShow:1,slidesToScroll:1,infinite:!1,arrows:!0,prevArrow:$(".arrow-perv"),nextArrow:$(".arrow-next")})}));